return [{
  json: {
    blocks: [
      {
        type: "section",
        text: {
          type: "mrkdwn",
          text: $json.message.content.summaryText
        },
        accessory: {
          type: "button",
          text: {
            type: "plain_text",
            text: `View ${$input.first().json.message.content.incidentId}`,
            emoji: true
          },
          value: $json.message.content.incidentId,
          url: $json.message.content.incidentLink,
          action_id: "button-action"
        }
      }
    ]
  }
}];

// This function runs in N8N's Code node
const cheerio = require("cheerio");

// Get the HTML content from the previous node
const htmlContent = items[0].json.body.content;

if (!htmlContent) {
  throw new Error("No HTML content found in the input");
}

// Load HTML into cheerio (similar to jQuery for Node.js)
const $ = cheerio.load(htmlContent);

// Extract the incident link from the anchor tag containing the incident ID
const incidentAnchor = $('a[href*="servicehealth?message="]').first();
const incidentLink = incidentAnchor.attr('href') || null;

// Remove unnecessary elements if needed
$('script, style, head, nav, footer, iframe, img').remove();

// Extract the text content, preserving basic structure
let plainText = '';

// Get the main content (customize selectors based on MS365 alert structure)
const mainContent = $('body') || $('main') || $('div.main-content');

// Process text with basic formatting
mainContent.find('h1, h2, h3, h4, h5, p, li, table, div').each((i, el) => {
  const tag = $(el).prop('tagName').toLowerCase();
  const text = $(el).text().trim();

  if (!text) return;

  // Add appropriate spacing and formatting
  if (tag.startsWith('h')) {
    plainText += `\n\n${text}\n\n`;
  } else if (tag === 'p') {
    plainText += `\n${text}\n`;
  } else if (tag === 'li') {
    plainText += `\n- ${text}`;
  } else {
    plainText += `\n${text}`;
  }
});

// Clean up extra whitespace and normalize line breaks
plainText = plainText.replace(/\s+\n/g, '\n').replace(/\n{3,}/g, '\n\n').trim();

// Set the output
return [{
  json: {
    extractedText: plainText,
    incidentLink: incidentLink
  }
}];
